import { View, StyleSheet, Dimensions, Image } from "react-native";
import { Button, Text } from '@ui-kitten/components';
import { MaterialCommunityIcons } from "@expo/vector-icons";

import { Screen } from '../components/Screen';
import { theme } from "../theme";

const LIST_MARGIN = 10;
const WIDTH = Dimensions.get("screen").width - LIST_MARGIN * 2;

export const SearchScreen = () => {
    const property = {
        images: [
            "https://images1.apartments.com/i2/uswEseKnMnU4ebRfW82ffRnkLFMx9-2IoNdmDEx12Lw/111/the-residences-at-newcity-chicago-il-building-photo.jpg",
            "https://images1.apartments.com/i2/6Z_l93QZ3QTdmrLCmpGezJmIH6yMN_MXF5OiacgZAE4/111/the-residences-at-newcity-chicago-il-building-photo.jpg",
        ],
        rentLow: 3750,
        rentHigh: 31054,
        bedroomLow: 1,
        bedroomHigh: 5,
        name: "The Hamilton",
        street: "555 NE 34th St",
        city: "Miami",
        state: "Florida",
        zip: 33137,
        tags: ["Parking","Value0", "Value1", "Value2"]
    }
    return (
        <Screen style={{ marginHorizontal: LIST_MARGIN }}>
            <View style={{borderColor: "#d3d3d3", borderRadius: 5, borderWidth: 1}}>
                <Image source={{ uri: property.images[0] }} style={{ height: 250, width: WIDTH }} />
                <View style={{ flexDirection: "row", justifyContent: "space-between" }}>
                    <Text category={"s1"}>
                        ${property.rentLow.toLocaleString()} - {" "}
                        {property.rentHigh.toLocaleString()}
                    </Text>
                    <MaterialCommunityIcons name="heart-outline" color={theme["color-primary-500"]} size={24} />
                </View>
                <Text category="c1">
                    {property.bedroomLow} - {property.bedroomHigh} Beds
                </Text>
                <Text category="c1" style={{ marginTop: 5 }}>
                    {property.name}
                </Text>
                <Text category="c1">
                    {property.street}
                </Text>
                <Text category="c1">
                    {property.city}, {property.state}, {property.zip}
                </Text>
                <Text category="c1" style={{ marginTop: 5 }}>
                    {property.tags.map((tag, index) => index === property.tags.length - 1 ? tag : `${tag}, `)}
                </Text>

                <View style={{flexDirection: "row", marginTop: 5, justifyContent: "space-evenly" }}>
                    <Button appearance="ghost" style={{borderColor: theme["color-primary-500"], width:"48%"}} size="small" onPress={()  => console.log("email")}>Email</Button>
                    <Button style={{width:"48%"}} size="small" onPress={()  => console.log("call")}>Call</Button>
                </View>
            </View>

        </Screen>
    )
}
